SELECT NM_CONTR.COD_EMPL,NM_CONTR.COD_GPRO,bi_emple.ape_empl,bi_emple.nom_empl,NM_CONTR.COD_AREA,NOM_AREA,'SIN EPS',IND_ACTI FROM NM_CONTR ,SO_AREAS,BI_EMPLE
WHERE BI_EMPLE.COD_EMPL=NM_CONTR.COD_EMPL AND NM_CONTR.COD_AREA=SO_AREAS.COD_AREA AND IND_ACTI in('A') AND NM_CONTR.COD_EMPL NOT IN(SELECT COD_EMPL FROM NM_CUENT WHERE TIP_ENTI='EPS' AND NM_CUENT.NRO_CONT = NM_CONTR.NRO_CONT)
UNION(
SELECT NM_CONTR.COD_EMPL,NM_CONTR.COD_GPRO,bi_emple.ape_empl,bi_emple.nom_empl,NM_CONTR.COD_AREA,NOM_AREA,'SIN AFP',IND_ACTI FROM NM_CONTR,SO_AREAS,BI_EMPLE
WHERE BI_EMPLE.COD_EMPL=NM_CONTR.COD_EMPL AND NM_CONTR.COD_AREA=SO_AREAS.COD_AREA AND IND_ACTI in('A')  AND NM_CONTR.COD_EMPL NOT IN(SELECT COD_EMPL FROM NM_CUENT WHERE TIP_ENTI='AFP' AND NM_CUENT.NRO_CONT = NM_CONTR.NRO_CONT ))
UNION(
SELECT NM_CONTR.COD_EMPL,NM_CONTR.COD_GPRO,bi_emple.ape_empl,bi_emple.nom_empl,NM_CONTR.COD_AREA,NOM_AREA,'SIN ARP',IND_ACTI FROM NM_CONTR,SO_AREAS,BI_EMPLE
